            x.setAttributeNS(null,"class","csl-22");
            x.setAttributeNS(null,"x","153.4");
            x.setAttributeNS(null,"y","151.5");
            x.setAttributeNS(null,"width","100");
            x.setAttributeNS(null,"height","140");
            svgItems = svgItems.appendChild(x);
        }
  
